#include <QUrl>

#include "controlpanelthirdpartypm.h"
#include "windowsregistry.h"
#include "wpmutils.h"
#include "packageutils.h"

ControlPanelThirdPartyPM::ControlPanelThirdPartyPM() : ignoreMSIEntries(true)
{
    detectionPrefix = "control-panel:";
}

void ControlPanelThirdPartyPM::scan(Job* job,
        std::vector<InstalledPackageVersion *> *installed,
        Repository *rep) const
{
    detectControlPanelProgramsFrom(installed, rep, HKEY_LOCAL_MACHINE,
            "SOFTWARE\\Microsoft\\Windows\\CurrentVersion\\Uninstall",
            false
    );
    if (WPMUtils::is64BitWindows()) {
        detectControlPanelProgramsFrom(installed, rep, HKEY_LOCAL_MACHINE,
                "SOFTWARE\\WoW6432Node\\Microsoft\\Windows\\CurrentVersion\\Uninstall",
                false
        );
    }
    detectControlPanelProgramsFrom(installed, rep, HKEY_CURRENT_USER,
            "SOFTWARE\\Microsoft\\Windows\\CurrentVersion\\Uninstall",
            false
    );
    if (WPMUtils::is64BitWindows()) {
        detectControlPanelProgramsFrom(installed, rep, HKEY_CURRENT_USER,
                "SOFTWARE\\WoW6432Node\\Microsoft\\Windows\\CurrentVersion\\Uninstall",
                false
        );
    }

    job->setProgress(1);
    job->complete();
}

void ControlPanelThirdPartyPM::
        detectControlPanelProgramsFrom(
        std::vector<InstalledPackageVersion*>* installed,
        Repository* rep, HKEY root,
        const QString& path, bool useWoWNode) const {
    WindowsRegistry wr;
    QString err;
    err = wr.open(root, path, useWoWNode, KEY_READ);
    if (err.isEmpty()) {
        QString fullPath;
        if (root == HKEY_CLASSES_ROOT)
            fullPath = "HKEY_CLASSES_ROOT";
        else if (root == HKEY_CURRENT_USER)
            fullPath = "HKEY_CURRENT_USER";
        else if (root == HKEY_LOCAL_MACHINE)
            fullPath = "HKEY_LOCAL_MACHINE";
        else if (root == HKEY_USERS)
            fullPath = "HKEY_USERS";
        else if (root == HKEY_PERFORMANCE_DATA)
            fullPath = "HKEY_PERFORMANCE_DATA";
        else if (root == HKEY_CURRENT_CONFIG)
            fullPath = "HKEY_CURRENT_CONFIG";
        else if (root == HKEY_DYN_DATA)
            fullPath = "HKEY_DYN_DATA";
        else
            fullPath = QString("%1").arg(reinterpret_cast<uintptr_t>(root));
        fullPath += "\\" + path;

        std::vector<QString> entries = wr.list(&err);
        for (auto& e: entries) {
            WindowsRegistry k;
            err = k.open(wr, e, KEY_READ);
            if (err.isEmpty()) {
                detectOneControlPanelProgram(installed, rep,
                        fullPath + "\\" + e, k, e);
            }
        }
    }
}

void ControlPanelThirdPartyPM::detectOneControlPanelProgram(
        std::vector<InstalledPackageVersion *> *installed,
        Repository *rep,
        const QString& registryPath,
        WindowsRegistry& k,
        const QString& keyName) const
{
    // see http://msdn.microsoft.com/en-us/library/aa372105(v=vs.85).aspx

    // find the package name
    QString package = keyName;
    package.replace('.', '_');
    package = PackageUtils::makeValidFullPackageName(
            "control-panel." + package);

    // find the version number
    bool versionFound = false;
    Version version;
    QString err;
    QString version_ = k.get("DisplayVersion", &err);
    if (err.isEmpty()) {
        if (version.setVersion(version_)) {
            version.normalize();
            versionFound = true;
        }
    }
    if (!versionFound) {
        DWORD major = k.getDWORD("VersionMajor", &err);
        if (err.isEmpty()) {
            DWORD minor = k.getDWORD("VersionMinor", &err);
            if (err.isEmpty())
                version.setVersion(static_cast<int>(major),
                        static_cast<int>(minor));
            else
                version.setVersion(static_cast<int>(major), 0);
            version.normalize();
            versionFound = true;
        }
    }
    if (!versionFound) {
        QString major = k.get("VersionMajor", &err);
        if (err.isEmpty()) {
            QString minor = k.get("VersionMinor", &err);
            if (err.isEmpty()) {
                if (version.setVersion(major)) {
                    versionFound = true;
                    version.normalize();
                }
            } else {
                if (version.setVersion(major + "." + minor)) {
                    versionFound = true;
                    version.normalize();
                }
            }
        }
    }
    if (!versionFound) {
        QString displayName = k.get("DisplayName", &err);
        if (err.isEmpty()) {
            std::vector<QString> parts = WPMUtils::split(displayName, ' ');
            if (parts.size() > 1 && parts.back().contains('.')) {
                version.setVersion(parts.back());
                version.normalize();
            }
        }
    }

    std::unique_ptr<Package> p(new Package(package, package));

    QString title = k.get("DisplayName", &err);
    if (!err.isEmpty() || title.isEmpty())
        title = keyName;

    if (this->cleanPackageTitles) {
        if (title.endsWith(version.getVersionString())) {
            title.chop(version.getVersionString().length());
            title = title.trimmed();
        }
    }

    p->title = title;

    p->description = p->title;

    qCDebug(npackd) << "ControlPanelThirdPartyPM::detectOneControlPanelProgram" <<
            package << title;

    QString publisher = k.get("Publisher", &err);
    if (err.isEmpty() && !publisher.isEmpty())
        p->description += " (" + publisher + ")";

    QString url = k.get("URLInfoAbout", &err);
    if (!err.isEmpty() || url.isEmpty() || !QUrl(url).isValid())
        url = "";
    if (url.isEmpty())
        url = k.get("URLUpdateInfo", &err);
    if (!err.isEmpty() || url.isEmpty() || !QUrl(url).isValid())
        url = "";
    p->url = url;

    QString changelog = k.get("URLUpdateInfo", &err);
    if (err.isEmpty() && WPMUtils::checkURL(QUrl(), &changelog, false).isEmpty())
        p->setChangeLog(changelog);

    p->categories.push_back(QObject::tr("Control panel software"));

    QString icon = k.get("DisplayIcon", &err);
    if (err.isEmpty())
        p->setIcon(WPMUtils::extractIconURL(icon));

    // Crystal Icons
    // p->icon = "data:image/png;base64,";

    //qCDebug(npackd) << "CP: adding package " << p.data()->name << p.data()->title;

    rep->savePackage(p.get(), true);

    QDir d;

    bool useThisEntry = true;

    QString uninstall;
    if (useThisEntry) {
        uninstall = k.get("QuietUninstallString", &err);
        if (!err.isEmpty())
            uninstall = "";
        if (uninstall.isEmpty())
            uninstall = k.get("UninstallString", &err);
        if (!err.isEmpty())
            uninstall = "";

        // some programs store in UninstallString the complete path to
        // the uninstallation program with spaces
        if (!uninstall.isEmpty() && uninstall.contains(" ") &&
                !uninstall.contains("\"") &&
                d.exists(uninstall))
            uninstall = "\"" + uninstall + "\"";

        if (uninstall.trimmed().isEmpty())
            useThisEntry = false;

        qCDebug(npackd) << uninstall;
    }

    // already detected as an MSI package
    if (ignoreMSIEntries) {
        if (uninstall.length() == 14 + 38 &&
                (uninstall.indexOf("MsiExec.exe /X", 0, Qt::CaseInsensitive) == 0 ||
                uninstall.indexOf("MsiExec.exe /I", 0, Qt::CaseInsensitive) == 0) &&
                WPMUtils::validateGUID(uninstall.right(38)) == "") {
            useThisEntry = false;
        }
    }

    QString dir;
    if (useThisEntry) {
        dir = k.get("InstallLocation", &err);
        if (!err.isEmpty() || dir.trimmed().isEmpty() || !d.exists(dir))
            dir = "";

        if (dir.isEmpty() && !uninstall.isEmpty()) {
            std::vector<QString> params = WPMUtils::parseCommandLine(uninstall, &err);
            // qCDebug(npackd) << uninstall << params.count();
            if (err.isEmpty() && params.size() > 0) {
                if (!params[0].trimmed().isEmpty() && d.exists(params[0])) {
                    dir = WPMUtils::parentDirectory(params[0]);
                }
            } /* DEBUG else {
                qCDebug(npackd) << "cannot parse " << uninstall << " " << err <<
                        " " << params.count();
                if (params.count() > 0)
                    qCDebug(npackd) << "cannot parse2 " << params[0] << " " <<
                            d.exists(params[0]);
            }*/
        }
    }

    if (useThisEntry) {
        if (!dir.isEmpty()) {
            dir = WPMUtils::normalizePath(dir, false);
        }
    }

    if (useThisEntry) {
        // qCDebug(npackd) << "adding " << registryPath << package << dir;

        // qCDebug(npackd) << package << version.getVersionString() << dir;
        InstalledPackageVersion* ipv = new InstalledPackageVersion(package,
                version, dir);
        ipv->detectionInfo = "control-panel:" + registryPath;
        installed->push_back(ipv);

        std::unique_ptr<PackageVersion> pv(new PackageVersion(package));
        pv->version = version;

        PackageVersionFile* pvf = new PackageVersionFile(
                ".Npackd\\Uninstall.bat", uninstall + "\r\n");
        pv->files.push_back(pvf);

        pvf = new PackageVersionFile(
                ".Npackd\\Stop.bat",
                "rem the program should be stopped by the uninstaller\r\n");
        pv->files.push_back(pvf);

        rep->savePackageVersion(pv.get(), true);
    }
}
