#include <memory>

#include <QString>

#include "installedpackagesthirdpartypm.h"
#include "installedpackages.h"

InstalledPackagesThirdPartyPM::InstalledPackagesThirdPartyPM()
{
}

void InstalledPackagesThirdPartyPM::scan(Job* job,
        std::vector<InstalledPackageVersion *> *installed, Repository *rep) const
{
    std::unique_ptr<InstalledPackages> ip(new InstalledPackages());
    QString err = ip->readRegistryDatabase();
    if (!err.isEmpty()) {
        job->setErrorMessage(err);
    }

    if (job->shouldProceed()) {
        std::vector<InstalledPackageVersion*> ipvs = ip->getAll();

        std::unordered_set<QString> used;
        QDir d;
        for (auto ipv: ipvs) {
            // this is different from all the other third party package managers.
            // We *know* that this was uninstalled.
            if (!d.exists(ipv->getDirectory()))
                continue;

            if (used.count(ipv->package) == 0) {
                QString title = ipv->package;
                int pos = title.lastIndexOf('.');
                if (pos > 1)
                    title = title.right(title.count() - pos - 1);
                Package* p = new Package(ipv->package, title);
                p->categories.push_back(
                        QObject::tr("Npackd list of installed packages"));
                p->description = p->title;
                // Crystal Icons
                // p->icon = "data:image/png;base64,";

                rep->packages.push_back(p);
                used.insert(ipv->package);
            }
            PackageVersion* pv = new PackageVersion(ipv->package, ipv->version);
            rep->packageVersions.push_back(pv);
            rep->package2versions.insert({ipv->package, pv});

            if (ipv->installed() && !ipv->package.startsWith("msi.") &&
                    !ipv->package.startsWith("control-panel.")) {
                // placing package versions of Windows in front so that
                // the directories are not used by something else
                if (ipv->package == "com.microsoft.Windows64" ||
                        ipv->package == "com.microsoft.Windows32" ||
                        ipv->package == "com.microsoft.Windows") {
                    installed->insert(installed->begin(), ipv->clone());
                } else {
                    installed->push_back(ipv->clone());
                }
            }
        }
        qDeleteAll(ipvs);

        job->setProgress(1);
    }

    job->complete();
}
